import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MusicServiceService } from '../service/music-service.service';
import { Artist } from '../models/Artist';

// The @Component decorator defines the metadata for the ListArtistsComponent
@Component({
	selector: 'app-list-artists', // The custom HTML tag used for this component
	templateUrl: './list-artists.component.html',  // The layout for the component
	styleUrls: ['./list-artists.component.css'] // The styles specific to this component
})
export class ListArtistsComponent implements OnInit {
	artists: Artist[] = []; // Stores the list of Artist objects for display
	selectedArtist: Artist | null = null;// Stores the currently selected Artist, if any

  // The constructor injects the ActivatedRoute and MusicServiceService
  // ActivatedRoute is used for accessing the route parameters,
  // MusicServiceService is the service providing the music data.
	constructor(private route: ActivatedRoute, private service: MusicServiceService) { }

  // ngOnInit is a lifecycle hook called after the component is initialized.
  // Here, we subscribe to query parameters and refresh the artists list when they change.
	ngOnInit() {
		this.route.queryParams.subscribe(params => {
			console.log("Getting data...");
			this.artists = this.service.getArtists();
			this.selectedArtist = null;
		});
	}
  // onSelectArtist is an event handler for selecting an artist from the list.
  // It updates the selectedArtist property and logs the artist's name to the console.
	public onSelectArtist(artist: Artist) {
		console.log("Selected Artist of " + artist.Name);
		this.selectedArtist = artist; // Update the selected artist
	}
}
