import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Album } from '../models/Album';


import { Artist } from '../models/Artist';

@Injectable({
  providedIn: 'root'
})
export class MusicServiceService {
  private readonly artists: Artist[] = [];
  private readonly albums: Album[] = [];

  constructor(private http: HttpClient) {}

  // Initializes the artists array with predefined data.
  private createArtists(): void {
    this.artists.push(new Artist(0, 'The Beatles'));
    // Add more artists as needed
  }

  // Retrieves all artists.
  public getArtists(): Artist[] {
    return this.artists;
  }

  public getAlbums(artistName: string): Observable<Album[]> {
    // Make an HTTP GET request to fetch albums for the given artist
    return this.http.get<Album[]>(`${this.apiUrl}?artist=${artistName}`);
  }

  // Retrieves a specific album by its ID.
  public getAlbum(id: number): Observable<Album> {
    return this.http.get<Album>(`/api/albums/${id}`);
  }

  // Adds a new album to the collection.
  public createAlbum(album: Album): Observable<Album> {
    return this.http.post<Album>('/api/albums', album);
  }


  // Deletes an album from the collection based on its ID.
  public deleteAlbum(id: number): Observable<void> {
    return this.http.delete<void>(`/api/albums/${id}`);
  }
}
