import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MusicServiceService } from '../service/music-service.service';

@Component({
  selector: 'app-delete-album',
  templateUrl: './delete-album.component.html',
  styleUrls: ['./delete-album.component.css']
})
export class DeleteAlbumComponent implements OnInit {
  constructor(
    private route: ActivatedRoute,
    private musicService: MusicServiceService,
    private router: Router
  ) { }

  ngOnInit() {
    const artist = this.route.snapshot.paramMap.get('artist');
    const id = Number(this.route.snapshot.paramMap.get('id'));
    if (artist && id) {
      this.deleteAlbum(id, artist);
    }
  }

  deleteAlbum(id: number, artist: string) {
    const result = this.musicService.deleteAlbum(id, artist);
    if (result === 0) {
      console.log('Album successfully deleted');
      // Navigate back to album list or artist list after deletion
      this.router.navigate(['/list-albums']);
    } else {
      console.log('Error deleting album');
      // Handle the error case, maybe stay on the page or show an error message
    }
  }
}
